import React from 'react';

const Stars = ({ starCount = 100, shootingStarCount = 5 }) => {
  const stars = Array.from({ length: starCount }).map((_, index) => ({
    id: index,
    top: `${Math.random() * 100}%`,
    left: `${Math.random() * 100}%`,
    size: `${Math.random() * 6 + 1}px`,
    animationDelay: `${Math.random() * 5}s`,
  }));

  const shootingStars = Array.from({ length: shootingStarCount }).map((_, index) => ({
    id: index,
    top: `${Math.random() * 100}%`,
    left: `${Math.random() * 100}%`,
    width: `${Math.random() * 20 + 10}px`,
    height: `${Math.random() * 20 + 10}px`,
    animationDuration: `${Math.random() * 5 + 2}s`,
    animationDelay: `${Math.random() * 5}s`,
    // Add some randomness to the movement
    angle: Math.random() * 360,
    speed: Math.random() * 2 + 1,
  }));

  return (
    <div className="fixed inset-0 overflow-hidden pointer-events-none">
      {stars.map((star) => (
        <div
          key={star.id}
          className="absolute bg-yellow-200 rounded-full animate-twinkle"
          style={{
            top: star.top,
            left: star.left,
            width: star.size,
            height: star.size,
            animationDelay: star.animationDelay,
          }}
        />
      ))}
      {shootingStars.map((shootingStar) => (
        <div
          key={shootingStar.id}
          className="absolute bg-yellow-200 rounded-full animate-shooting-star"
          style={{
            top: shootingStar.top,
            left: shootingStar.left,
            width: shootingStar.width,
            height: shootingStar.height,
            animationDuration: shootingStar.animationDuration,
            animationDelay: shootingStar.animationDelay,
            // Add some randomness to the movement
            transform: `rotate(${shootingStar.angle}deg)`,
            animationName: `shootingStar${shootingStar.id}`,
          }}
        />
      ))}
    </div>
  );
};

export default Stars;
