import React, { useEffect } from 'react';

const ShootingStar = ({ top, left }) => {
  useEffect(() => {
    const timer = setTimeout(() => {
      // Cleanup function to remove the shooting star after animation
      document.getElementById('shooting-stars-container').removeChild(document.getElementById('shooting-star'));
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div
      id="shooting-star"
      className="shooting-star"
      style={{ top: `${top}%`, left: `${left}%` }}
    />
  );
};

const ShootingStars = () => {
  const shootingStarInterval = 8000; // Interval between shooting stars in milliseconds

  const createShootingStar = () => {
    const top = Math.random() * 100;
    const left = Math.random() * 100;
    return <ShootingStar key={Date.now()} top={top} left={left} />;
  };

  useEffect(() => {
    const interval = Math.random() * shootingStarInterval;

    const shootingStarIntervalId = setInterval(() => {
      document.getElementById('shooting-stars-container').appendChild(createShootingStar().props.children);
    }, interval);

    return () => clearInterval(shootingStarIntervalId);
  }, []);

  return <div id="shooting-stars-container"></div>;
};

export default ShootingStars;
