import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Main.css"; // We will create this CSS file for styling

const Main = () => {
    const [userName, setUserName] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch user data from localStorage or session storage
        const user = JSON.parse(localStorage.getItem("user")); // Assuming user data is stored in localStorage
        if (user && user.firstName) {
            setUserName(user.firstName);
        } else {
            navigate("/login"); // Redirect to login if no user data is found
        }
    }, [navigate]);

    return (
        <div className="main-container">
            <div className="greeting-card">
                <h1 className="greeting-text">Hello, {userName}!</h1>
                <p className="welcome-message">Welcome to your dashboard.</p>
            </div>
        </div>
    );
};

export default Main;
