import React, { useState } from 'react';

const teamData = {
  Leadership: [
    { name: 'Lizi', role: 'Designer', img: 'url-to-image' },
    { name: 'Shelmah Chebet', role: 'Designer & Event Coordinator', img: 'url-to-image' },
    { name: 'Fiona', role: 'Lead Event Coordinator & Lead Marketing Team', img: 'url-to-image' },
  ],
  Design: [
    { name: 'Lizi', role: 'Designer', img: 'url-to-image' },
    // Add other team members under Design
  ],
  Marketing: [
    { name: 'Shelmah Chebet', role: 'Designer & Event Coordinator', img: 'url-to-image' },
    { name: 'Fiona', role: 'Lead Event Coordinator & Lead Marketing Team', img: 'url-to-image' },
    // Add other team members under Marketing
  ],
  Development: [
    // Add team members under Development
  ],
};

function Team() {
  const [activeCategory, setActiveCategory] = useState('Marketing'); // Default to Marketing

  return (
    <div className="text-center py-8">
      <h1 className='sm:mt-10 lg:mt-20 text-5xl sm:text-8xl md:text-8xl text-center font-potta-one font-normal leading-none text-[#f9f5e3]'> Applications </h1>
      <div className="flex justify-center space-x-4 mb-8">
        {Object.keys(teamData).map((category) => (
          <div
            key={category}
            className={`cursor-pointer rounded-full p-4 ${activeCategory === category ? 'bg-yellow-500' : 'bg-gray-700'}`}
            onClick={() => setActiveCategory(category)}
          >
            <div className="text-xl font-semibold">{category}</div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
        {teamData[activeCategory].map((member, index) => (
          <div key={index} className="bg-white text-black p-4 rounded-lg shadow-lg">
            <img src={member.img} alt={member.name} className="w-full h-48 object-cover rounded-lg mb-4" />
            <h3 className="text-xl font-semibold">{member.name}</h3>
            <p className="text-gray-700">{member.role}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Team;
