import React, { useState } from 'react';
import m1 from '../assets/Moon1.svg';
import m2 from '../assets/Moon2.svg';
import m3 from '../assets/Moon3.svg';
import m4 from '../assets/Moon4.svg';
import m5 from '../assets/Moon5.svg';
import m6 from '../assets/Moon6.svg';
import m7 from '../assets/Moon7.svg';
import leftArrow from '../assets/left-arrow.svg'; // Import your left arrow image
import rightArrow from '../assets/right-arrow.svg'; // Import your right arrow image

const teamData = {
    Leadership: {
      image: m1, 
      members: [
        { name: 'Lizi', role: 'Designer', img: 'url-to-lizi-image' },
        { name: 'Shelmah Chebet', role: 'Designer & Event Coordinator', img: 'url-to-shelmah-image' },
        { name: 'Fiona', role: 'Lead Event Coordinator & Lead Marketing Team', img: 'url-to-fiona-image' },
      ],
    },
    Design: {
      image: m2, 
      members: [
        { name: 'Lizi', role: 'Designer', img: 'url-to-lizi-image' },
      ],
    },
    Marketing: {
      image: m3, 
      members: [
        { name: 'Shelmah Chebet', role: 'Designer & Event Coordinator', img: 'url-to-shelmah-image' },
        { name: 'Fiona', role: 'Lead Event Coordinator & Lead Marketing Team', img: 'url-to-fiona-image' },
      ],
    },
    Development: {
      image: m4, 
      members: [
        // Add team members under Development
      ],
    },
    HR: {
      image: m5,
      members: [
        // Add team members under HR
      ],
    },
    Operations: {
      image: m6,
      members: [
        // Add team members under Operations
      ],
    },
    Finance: {
      image: m7,
      members: [
        // Add team members under Finance
      ],
    },
};

function Team() {
    const [activeCategory, setActiveCategory] = useState('Marketing'); // Default to Marketing
    const categories = Object.keys(teamData); // Get the list of all categories
    const activeIndex = categories.indexOf(activeCategory); // Get the index of the current active category

    const handleNext = () => {
        const nextIndex = (activeIndex + 1) % categories.length;
        setActiveCategory(categories[nextIndex]);
    };

    const handlePrevious = () => {
        const previousIndex = (activeIndex - 1 + categories.length) % categories.length;
        setActiveCategory(categories[previousIndex]);
    };

    return (
        <div className="text-center py-8">
            <h1 className="sm:mt-10 lg:mt-20 text-5xl sm:text-8xl md:text-8xl text-center font-potta-one font-normal leading-none text-[#f9f5e3]">
                Applications
            </h1>

            <div className="flex justify-center items-center space-x-4 mb-8">
                {/* Left Arrow */}
                <img
                    src={leftArrow}
                    alt="Previous"
                    className="w-10 h-10 cursor-pointer"
                    onClick={handlePrevious}
                />

                {/* Display Department Moons */}
                <div className="flex space-x-4">
                    {categories.map((category, index) => (
                        <div
                            key={category}
                            className="text-center"
                            onClick={() => setActiveCategory(category)}
                        >
                            <img 
                              src={teamData[category].image} 
                              alt={`${category} department`} 
                              className={`w-20 h-20 md:w-32 md:h-32 object-cover rounded-full mx-auto mb-2 cursor-pointer ${activeCategory === category ? 'border-4 border-yellow-500' : ''}`}
                            />
                            <div className={`cursor-pointer text-xl font-semibold ${activeCategory === category ? 'text-yellow-500' : 'text-gray-700'}`}>
                              {category}
                            </div>
                        </div>
                    ))}
                </div>

                {/* Right Arrow */}
                <img
                    src={rightArrow}
                    alt="Next"
                    className="w-10 h-10 cursor-pointer text-white"
                    onClick={handleNext}
                />
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
                {teamData[activeCategory].members.map((member, index) => (
                    <div key={index} className="bg-white text-black p-4 rounded-lg shadow-lg">
                        <img src={member.img} alt={member.name} className="w-full h-48 object-cover rounded-lg mb-4" />
                        <h3 className="text-xl font-semibold">{member.name}</h3>
                        <p className="text-gray-700">{member.role}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Team;
