import React, { useEffect, useState } from 'react';
import styles from './styles.module.css';

const Main = () => {
  const [firstName, setFirstName] = useState('');

  useEffect(() => {
    // Assuming the first name is stored in localStorage after login
    const storedFirstName = localStorage.getItem('firstName');

    // If you need to fetch it from an API, you would do that here instead
    if (storedFirstName) {
      setFirstName(storedFirstName);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('firstName');
    window.location.reload();
  };

  return (
    <div className="bg-gray-100 w-screen h-screen flex flex-col items-center justify-center">
      <nav className={`${styles.navbar} w-full flex justify-between items-center p-4 bg-blue-600 text-white`}>
        <h1 className="text-2xl font-bold">Fakebook</h1>
        <button className={styles.white_btn} onClick={handleLogout}>
          Logout
        </button>
      </nav>
      <div className="mt-10">
        {firstName && <h2 className="text-3xl font-semibold">Welcome, {firstName}!</h2>}
      </div>
    </div>
  );
};

export default Main;
