    useEffect(() => {
        // Fetch user data from localStorage or session storage
        const user = JSON.parse(localStorage.getItem("user")); // Assuming user data is stored in localStorage

        if (user && user.firstName) {
            setUserName(user.firstName);
        } else {
            // Delay the redirection to see if the user data is really missing
            setTimeout(() => {
                navigate("/login");
            }, 1000); // 1 second delay
        }
    }, [navigate]);

    return (
        <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-cyan-400 to-blue-500">
            <div className="bg-white p-8 md:p-12 rounded-lg shadow-lg text-center max-w-xs md:max-w-md lg:max-w-lg">
                <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-4">Hello, {userName}!</h1>
                <p className="text-lg md:text-xl text-gray-600">Welcome to your dashboard.</p>
            </div>
        </div>
    );
};

export default Main;
