import React, { useEffect, useState } from 'react';

import '../App.css';


const Stars = ({ starCount = 100, shootingStarCount = 5 }) => {
    const [shootingStars, setShootingStars] = useState([]);

    const stars = Array.from({ length: starCount }).map((_, index) => ({
      id: index,
      top: `${Math.random() * 100}%`,
      left: `${Math.random() * 100}%`,
      size: `${Math.random() * 3 + 1}px`,
      animationDelay: `${Math.random() * 5}s`,
    }));

    useEffect(() => {
      const createShootingStar = () => ({
        id: Math.random(),
        top: `${Math.random() * 50}%`,
        left: '-10%',
        animationDuration: `${Math.random() * 3 + 2}s`,
      });

      const initialShootingStars = Array.from({ length: shootingStarCount }, createShootingStar);
      setShootingStars(initialShootingStars);

      const interval = setInterval(() => {
        setShootingStars((prevStars) => [
          ...prevStars.slice(1),
          createShootingStar(),
        ]);
      }, 3000);

      return () => clearInterval(interval);
    }, [shootingStarCount]);

    return (
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        {stars.map((star) => (
          <div
            key={star.id}
            className="star"
            style={{
              top: star.top,
              left: star.left,
              width: star.size,
              height: star.size,
              animationDelay: star.animationDelay,
            }}
          />
        ))}
        {shootingStars.map((star) => (
          <div
            key={star.id}
            className="shooting-star"
            style={{
              top: star.top,
              left: star.left,
              animationDuration: star.animationDuration,
            }}
          />
        ))}
      </div>
    );
  };

export default Stars;
