import React from 'react';

const Stars = ({ starCount = 100 }) => {
  const stars = Array.from({ length: starCount }).map((_, index) => {
    if (index % 10 === 0) { // Create shooting star every 10th star
      return {
        id: index,
        top: `${Math.random() * 100}%`,
        left: `${Math.random() * 100}%`,
        size: '3px', // Fixed size for shooting stars
        animationDelay: `${Math.random() * 5}s`,
        animation: 'shoot 5s linear forwards', // Add animation name
      };
    } else {
      return {
        id: index,
        top: `${Math.random() * 100}%`,
        left: `${Math.random() * 100}%`,
        size: `${Math.random() * 6 + 1}px`, // Vary regular star sizes
        animationDelay: `${Math.random() * 5}s`,
      };
    }
  });

  return (
    <div className="fixed inset-0 overflow-hidden pointer-events-none">
      {stars.map((star) => (
        <div
          key={star.id}
          className={`absolute rounded-full ${star.animation ? 'shooting-star' : 'regular-star'}`} // Add class based on animation
          style={{
            top: star.top,
            left: star.left,
            width: star.size,
            height: star.size,
            animationDelay: star.animationDelay,
          }}
        />
      ))}
    </div>
  );
};

export default Stars;
