import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';


const Stars = ({ starCount = 100, shootingStarCount = 5 }) => {
    const mountRef = useRef(null);

    useEffect(() => {
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });

        renderer.setSize(window.innerWidth, window.innerHeight);
        mountRef.current.appendChild(renderer.domElement);

        const geometry = new THREE.BufferGeometry();
        const vertices = [];

        for (let i = 0; i < starCount; i++) {
            vertices.push(
                THREE.MathUtils.randFloatSpread(2000),
                THREE.MathUtils.randFloatSpread(2000),
                THREE.MathUtils.randFloatSpread(2000)
            );
        }

        geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));

        const material = new THREE.PointsMaterial({ color: 0xffffff, size: 2 });
        const stars = new THREE.Points(geometry, material);
        scene.add(stars);

        const shootingStars = [];
        for (let i = 0; i < shootingStarCount; i++) {
            const starGeometry = new THREE.SphereGeometry(0.1, 24, 24);
            const starMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
            const shootingStar = new THREE.Mesh(starGeometry, starMaterial);
            shootingStar.position.set(
                THREE.MathUtils.randFloatSpread(2000),
                THREE.MathUtils.randFloatSpread(2000),
                THREE.MathUtils.randFloatSpread(2000)
            );
            shootingStars.push(shootingStar);
            scene.add(shootingStar);
        }

        camera.position.z = 5;

        const animate = () => {
            requestAnimationFrame(animate);

            stars.rotation.x += 0.001;
            stars.rotation.y += 0.001;

            shootingStars.forEach((star) => {
                star.position.x -= 0.1;
                star.position.y -= 0.1;
                if (star.position.x < -1000 || star.position.y < -1000) {
                    star.position.set(
                        THREE.MathUtils.randFloatSpread(2000),
                        THREE.MathUtils.randFloatSpread(2000),
                        THREE.MathUtils.randFloatSpread(2000)
                    );
                }
            });

            renderer.render(scene, camera);
        };

        animate();

        const handleResize = () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        };

        window.addEventListener('resize', handleResize);

        return () => {
            mountRef.current.removeChild(renderer.domElement);
            window.removeEventListener('resize', handleResize);
        };
    }, [starCount, shootingStarCount]);

    return <div ref={mountRef} className="sky-gradient fixed inset-0 overflow-hidden pointer-events-none"></div>;
};

export default Stars;
