import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const Stars = ({ starCount = 100, shootingStarCount = 5 }) => {
    const mountRef = useRef(null);

    useEffect(() => {
        // Create scene, camera, and renderer
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        mountRef.current.appendChild(renderer.domElement);

        // Create stars
        const starGeometry = new THREE.BufferGeometry();
        const starVertices = [];
        for (let i = 0; i < starCount; i++) {
            starVertices.push(
                THREE.MathUtils.randFloatSpread(2000),
                THREE.MathUtils.randFloatSpread(2000),
                THREE.MathUtils.randFloatSpread(2000)
            );
        }
        starGeometry.setAttribute('position', new THREE.Float32BufferAttribute(starVertices, 3));
        const starMaterial = new THREE.PointsMaterial({ color: 0xffffff, size: 3, sizeAttenuation: true });
        const stars = new THREE.Points(starGeometry, starMaterial);
        scene.add(stars);

        // Create shooting star
        const createShootingStar = () => {
            const start = new THREE.Vector3(
                THREE.MathUtils.randFloatSpread(2000),
                THREE.MathUtils.randFloatSpread(2000),
                THREE.MathUtils.randFloatSpread(2000)
            );
            const end = new THREE.Vector3(
                start.x - 2000 * Math.random(),
                start.y - 2000 * Math.random(),
                start.z
            );
            const curve = new THREE.CatmullRomCurve3([start, end]);
            const tubeGeometry = new THREE.TubeGeometry(curve, 64, 0.1, 8, false);
            const shaderMaterial = new THREE.ShaderMaterial({
                uniforms: {
                    color: { value: new THREE.Color(0xADD8E6) },
                    time: { value: 0 }
                },
                vertexShader: `
                    varying vec3 vPosition;
                    void main() {
                        vPosition = position;
                        gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
                    }
                `,
                fragmentShader: `
                    uniform vec3 color;
                    uniform float time;
                    varying vec3 vPosition;
                    void main() {
                        float alpha = 1.0 - (vPosition.z / 1000.0);
                        vec3 glow = color * (1.0 - length(vPosition) / 1000.0);
                        gl_FragColor = vec4(glow, alpha * 0.75);
                    }
                `,
                transparent: true
            });
            const shootingStarLine = new THREE.Mesh(tubeGeometry, shaderMaterial);
            scene.add(shootingStarLine);
            return { line: shootingStarLine, speed: 5 + Math.random() * 5, start, end };
        };

        const activeShootingStars = [];

        // Set camera position
        camera.position.z = 5;

        // Animation loop
        const animate = () => {
            requestAnimationFrame(animate);

            stars.rotation.x += 0.001;
            stars.rotation.y += 0.001;

            activeShootingStars.forEach((star, index) => {
                const direction = new THREE.Vector3().subVectors(star.end, star.start).normalize();
                star.line.position.add(direction.multiplyScalar(star.speed));
                star.line.material.uniforms.time.value += 0.1;

                if (star.line.position.distanceTo(star.end) < 10) {
                    scene.remove(star.line);
                    activeShootingStars.splice(index, 1);
                }
            });

            if (Math.random() < 0.01) {
                activeShootingStars.push(createShootingStar());
            }

            renderer.render(scene, camera);
        };

        animate();

        // Handle window resize
        const handleResize = () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        };
        window.addEventListener('resize', handleResize);

        return () => {
            mountRef.current.removeChild(renderer.domElement);
            window.removeEventListener('resize', handleResize);
        };
    }, [starCount, shootingStarCount]);

    return <div ref={mountRef} className="sky-gradient fixed inset-0 overflow-hidden pointer-events-none"></div>;
};

export default Stars;
