import React from 'react';
import '../App.css';

const Stars = ({ starCount = 100, shootingStarCount = 5 }) => {
  const stars = Array.from({ length: starCount }).map((_, index) => ({
    id: index,
    top: `${Math.random() * 100}%`,
    left: `${Math.random() * 100}%`,
    size: `${Math.random() * 6 + 1}px`, // Vary the star sizes between 1px and 4px
    animationDelay: `${Math.random() * 5}s`,
  }));

  const shootingStars = Array.from({ length: shootingStarCount }).map((_, index) => ({
    id: index,
    top: `${Math.random() * 100}%`,
    left: `${Math.random() * 100}%`,
    size: `${Math.random() * 6 + 1}px`, // Vary shooting star sizes
    direction: Math.random() < 0.5 ? 'left' : 'right', // Random direction
    animationDuration: `${Math.random() * 3 + 2}s`, // Animation duration between 2s and 5s
  }));

  return (
    <div className="fixed inset-0 overflow-hidden pointer-events-none">
      {stars.map((star) => (
        <div
          key={star.id}
          className="absolute bg-yellow-200 rounded-full animate-twinkle"
          style={{
            top: star.top,
            left: star.left,
            width: star.size,
            height: star.size,
            animationDelay: star.animationDelay,
          }}
        />
      ))}
      {shootingStars.map((shootingStar) => (
        <div
          key={shootingStar.id}
          className="absolute bg-white rounded-full" // White color for shooting stars
          style={{
            top: shootingStar.top,
            left: shootingStar.left,
            width: shootingStar.size,
            height: shootingStar.size,
            transform: 'scale(0.5)', // Initial scale for shooting star
            animation: `shootingStar ${shootingStar.animationDuration} linear`,
            animationFillMode: 'forwards', // Maintain final state after animation
          }}
        />
      ))}
    </div>
  );
};



export default Stars;
