import React, { useEffect, useState } from 'react';


const About = () => {
  const targetDate = '2024-12-25T00:00:00'; // Set your target date here

  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft(targetDate));

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft(calculateTimeLeft(targetDate));
    }, 1000);

    return () => clearInterval(timer);
  }, [targetDate]);

  return (
    <div className="p-4">
      <div className="text-center font-potta-one text-8xl font-normal leading-none text-[#f9f5e3]">
        Schedule
      </div>
      <div className="flex items-center justify-center text-white">
        <div className="text-center">
          <div className="bg-gray-800 p-8 rounded-lg shadow-lg inline-block">
            <div className="flex space-x-4">
              <TimeUnit value={timeLeft.days} label="DAYS" />
              <Separator />
              <TimeUnit value={timeLeft.hours} label="HRS" />
              <Separator />
              <TimeUnit value={timeLeft.minutes} label="MINS" />
              <Separator />
              <TimeUnit value={timeLeft.second} label="MINS" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const TimeUnit = ({ value, label }) => (
  <div>
    <div className="text-xl">{label}</div>
    <div className="text-3xl">{value}</div>
  </div>
);

const Separator = () => <div className="text-3xl">:</div>;

const calculateTimeLeft = (targetDate) => {
  const target = new Date(targetDate).getTime();
  const now = new Date().getTime();
  const distance = target - now;

  if (distance < 0) {
    return { days: 0, hours: 0, minutes: 0 };
  }

  const days = Math.floor(distance / (1000 * 60 * 60 * 24));
  const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
  const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));

  return { days, hours, minutes };
};

export default About;
