import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const Stars = ({ starCount = 100 }) => {
    const mountRef = useRef(null);

    useEffect(() => {
        // Create scene, camera, and renderer
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        renderer.setPixelRatio(window.devicePixelRatio); // Ensure native resolution
        mountRef.current.appendChild(renderer.domElement);

        // Create stars
        const starGeometry = new THREE.BufferGeometry();
        const starVertices = [];
        for (let i = 0; i < starCount; i++) {
            starVertices.push(
                THREE.MathUtils.randFloatSpread(2000),
                THREE.MathUtils.randFloatSpread(2000),
                THREE.MathUtils.randFloatSpread(2000)
            );
        }
        starGeometry.setAttribute('position', new THREE.Float32BufferAttribute(starVertices, 3));
        const starMaterial = new THREE.PointsMaterial({
            color: 0xffffff,
            size: 2,
            sizeAttenuation: true,
            transparent: true,
            opacity: 0.9,
        });
        const stars = new THREE.Points(starGeometry, starMaterial);
        scene.add(stars);

        // Shooting Star creation
        class ShootingStar {
            constructor() {
                this.start = new THREE.Vector3(
                    THREE.MathUtils.randFloatSpread(2000),
                    THREE.MathUtils.randFloatSpread(2000),
                    THREE.MathUtils.randFloatSpread(2000)
                );
                this.end = new THREE.Vector3(
                    this.start.x - 2000 * Math.random(),
                    this.start.y - 2000 * Math.random(),
                    this.start.z
                );
                this.color = ['#a1ffba', '#a1d2ff', '#fffaa1', '#ffa1a1'][Math.floor(Math.random() * 4)];
                this.geometry = new THREE.BufferGeometry().setFromPoints([this.start, this.end]);
                this.material = new THREE.ShaderMaterial({
                    uniforms: {
                        color: { value: new THREE.Color(this.color) },
                        time: { value: 0 },
                    },
                    vertexShader: `
                        varying vec3 vPosition;
                        void main() {
                            vPosition = position;
                            gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
                        }
                    `,
                    fragmentShader: `
                        uniform vec3 color;
                        uniform float time;
                        varying vec3 vPosition;
                        void main() {
                            float alpha = 2.0 - (length(vPosition) / 1000.0);
                            float intensity = 5.0 - length(vPosition) / 1000.0;
                            vec3 glow = color * intensity * intensity;

                            // Enhance the tail
                            float tailIntensity = smoothstep(0.9, 0.7, length(vPosition));
                            glow *= tailIntensity;

                            // Brighter and more focused head
                            float headIntensity = smoothstep(0.1, 0.0, length(vPosition));
                            glow += color * headIntensity * 5.0;

                            // Vary color along the tail
                            glow *= mix(vec3(1.0, 1.0, 1.0), vec3(0.8, 0.8, 1.0), vPosition.z / 1000.0);

                            gl_FragColor = vec4(glow, alpha);
                        }
                    `,
                    transparent: true,
                    blending: THREE.AdditiveBlending,
                    depthTest: false,
                });

                this.line = new THREE.Line(this.geometry, this.material);
                this.speed = 10 + Math.random() * 5;
                scene.add(this.line);
            }

            update() {
                const direction = new THREE.Vector3().subVectors(this.end, this.start).normalize();
                this.line.position.add(direction.multiplyScalar(this.speed));
                this.material.uniforms.time.value += 0.1;

                if (this.line.position.distanceTo(this.end) < 10) {
                    scene.remove(this.line);
                    return false;
                }
                return true;
            }
        }

        const activeShootingStars = [];

        // Set camera position
        camera.position.z = 5;

        // Animation loop
        const animate = () => {
            requestAnimationFrame(animate);

            stars.rotation.x += 0.001;
            stars.rotation.y += 0.001;

            for (let i = activeShootingStars.length - 1; i >= 0; i--) {
                if (!activeShootingStars[i].update()) {
                    activeShootingStars.splice(i, 1);
                }
            }

            if (Math.random() < 0.02) {
                activeShootingStars.push(new ShootingStar());
            }

            renderer.render(scene, camera);
        };

        animate();

        // Handle window resize
        const handleResize = () => {
            const { innerWidth, innerHeight } = window;
            camera.aspect = innerWidth / innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(innerWidth, innerHeight);
        };
        window.addEventListener('resize', handleResize);

        return () => {
            mountRef.current.removeChild(renderer.domElement);
            window.removeEventListener('resize', handleResize);
        };
    }, [starCount]);

    return <div ref={mountRef} className="sky-gradient fixed inset-0 overflow-hidden pointer-events-none"></div>;
};

export default Stars;
