import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Main = () => {
    const [firstname, setFirstname] = useState('');
    const [error, setError] = useState(null); // Track any errors
    const navigate = useNavigate();

    useEffect(() => {
        const fetchFirstname = async () => {
            try {
                const token = localStorage.getItem('token'); // Retrieve token from localStorage
                if (!token) {
                  setError('No token found. Redirecting to login...');
                  setTimeout(() => navigate('/login'), 3000); // Redirect to login after 3 seconds
                  return;
                }

                const response = await axios.get('/api/firstname', {
                    headers: {
                      Authorization: `Bearer ${token}`, // Send the token in the Authorization header
                    },
                });

                if (response.data.firstname) {
                  setFirstname(response.data.firstname); // Set the first name from the response
                } else {
                  setError('Could not retrieve the first name.');
                }
            } catch (error) {
              console.error('Error fetching firstname:', error);
              setError('Failed to fetch data. Please try again.');
              setTimeout(() => navigate('/login'), 3000); // Redirect to login after 3 seconds
            }
        };

        fetchFirstname();
    }, [navigate]);

    if (error) {
        return <div>{error}</div>; // Display the error message if there is one
    }

    return <h1 className='text-8xl'>Hello, {firstname}</h1>;
};

export default Main;
