import React, { useState, useEffect } from 'react';

const CountdownTimer = ({ targetDate }) => {
  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

  function calculateTimeLeft() {
    const difference = +new Date(targetDate) - +new Date();
    let timeLeft = {};

    if (difference > 0) {
      timeLeft = {
        days: Math.floor(difference / (1000 * 60 * 60 * 24)),
        hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
        minutes: Math.floor((difference / 1000 / 60) % 60),
      };
    }

    return timeLeft;
  }

  useEffect(() => {
    const timer = setTimeout(() => {
      setTimeLeft(calculateTimeLeft());
    }, 1000);

    return () => clearTimeout(timer);
  });

  const addLeadingZero = (value) => {
    return value < 10 ? `0${value}` : value;
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-purple-900 text-white p-4">
      <div className="text-5xl font-bold mb-8">Schedule</div>
      <div className="bg-gray-800 rounded-lg p-6 shadow-lg">
        <div className="grid grid-cols-3 gap-4 text-center">
          <div className="text-xl font-semibold">DAYS</div>
          <div className="text-xl font-semibold">HRS</div>
          <div className="text-xl font-semibold">MINS</div>
          <div className="text-4xl font-bold">{addLeadingZero(timeLeft.days)}</div>
          <div className="text-4xl font-bold">:</div>
          <div className="text-4xl font-bold">{addLeadingZero(timeLeft.hours)}</div>
          <div className="text-4xl font-bold">:</div>
          <div className="text-4xl font-bold">{addLeadingZero(timeLeft.minutes)}</div>
        </div>
      </div>
    </div>
  );
};

export default CountdownTimer;
